CREATE PROCEDURE  ESRS.SP_INSERT_ITEM_INSTANCE(IN a_SERIAL_NUMBER VARCHAR(500), IN a_PRODUCT_TYPE VARCHAR(500), IN a_SITE_ID VARCHAR(500),IN a_SERVICE_DUNS VARCHAR(500),IN a_INSTALL_DUNS VARCHAR(500),OUT p_return_value VARCHAR(500)) MODIFIES SQL DATA BEGIN ATOMIC DECLARE v_csi_product_type   VARCHAR(64); DECLARE v_part_number  VARCHAR(12);  SELECT esrs_product_type INTO v_csi_product_type FROM ESRS.PRODUCT_TYPE WHERE UPPER (drm_product_type) = UPPER (a_PRODUCT_TYPE); INSERT INTO ESRS.ITEM_INSTANCE (ITEM_INSTANCE_ID,PARTY_NUMBER,SERIAL_NUMBER,PART_NUMBER,PRODUCT_TYPE,MODEL,PART_DESCRIPTION,PART_TYPE,ITEM_STATUS,SERVICE_DUNS,INSTALL_DUNS) VALUES ('1',a_SITE_ID,a_SERIAL_NUMBER,v_part_number,v_csi_product_type,v_csi_product_type,'SWID CREATED BY ELMS',v_csi_product_type,'CREATED',a_SERVICE_DUNS,a_INSTALL_DUNS);INSERT INTO ESRS.ITEM_INSTANCE_WITH_DUPES (ITEM_INSTANCE_ID,PARTY_NUMBER,SERIAL_NUMBER,PART_NUMBER,PRODUCT_TYPE,MODEL,PART_DESCRIPTION,PART_TYPE,ITEM_STATUS,SERVICE_DUNS,INSTALL_DUNS,CREATE_DATE,LAST_UPD_DATE) VALUES ('1',a_SITE_ID,a_SERIAL_NUMBER,v_part_number,v_csi_product_type,v_csi_product_type,'SWID CREATED BY ELMS',v_csi_product_type,'CREATED',a_SERVICE_DUNS,a_INSTALL_DUNS,SYSDATE,SYSDATE);  SET p_return_value = 'SUCCESS';END